def_ranks <- player_stats_def_99_24 %>% 
  group_by(season, team, player_id, player_display_name, position, position_group) %>% 
  summarise(across(starts_with('def'),
                   list(
                     sum = ~sum(., na.rm = TRUE)
                   ),
                   .names = '{.fn}_{.col}'
                   ),
            distinct_weeks = n_distinct(week),
            .groups = 'drop'
            )
  # group_by(season) %>% 
