  nflplotR::gt_nfl_logos(columns = "team") %>% 
  gt_merge_stack(col1 = player_display_name, col2 = position.x) %>%
  tab_style(
    style = list(
      cell_fill(color = "gold"),
      cell_text(weight = "bold")
    ),
    locations = cells_body(
      rows = player_display_name == 'Cameron Heyward'
